import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { updatePassword } from 'aws-amplify/auth';

const ChangePasswordPage = () => {
  const [currentPassword, setCurrentPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [isChanging, setIsChanging] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleChangePassword = async (e) => {
    e.preventDefault();
    setError('');

    // ÏûÖÎ†• Í≤ÄÏ¶ù
    if (!currentPassword || !newPassword || !confirmPassword) {
      setError('Î™®Îì† ÌïÑÎìúÎ•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    if (newPassword !== confirmPassword) {
      setError('ÏÉà ÎπÑÎ∞ÄÎ≤àÌò∏ÏôÄ ÌôïÏù∏ ÎπÑÎ∞ÄÎ≤àÌò∏Í∞Ä ÏùºÏπòÌïòÏßÄ ÏïäÏäµÎãàÎã§.');
      return;
    }

    // ÎπÑÎ∞ÄÎ≤àÌò∏ Î≥µÏû°ÏÑ± Í≤ÄÏ¶ù (Cognito ÏöîÍµ¨ÏÇ¨Ìï≠)
    // ÏµúÏÜå 6Ïûê, ÏÜåÎ¨∏Ïûê, Ïà´Ïûê, ÌäπÏàòÎ¨∏Ïûê Ìè¨Ìï® (ÎåÄÎ¨∏ÏûêÎäî ÏÑ†ÌÉùÏÇ¨Ìï≠)
    const passwordRegex = /^(?=.*[a-z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{6,}$/;
    if (!passwordRegex.test(newPassword)) {
      setError('ÎπÑÎ∞ÄÎ≤àÌò∏Îäî ÏµúÏÜå 6Ïûê Ïù¥ÏÉÅÏù¥Î©∞, ÏÜåÎ¨∏Ïûê, Ïà´Ïûê, ÌäπÏàòÎ¨∏ÏûêÎ•º Ìè¨Ìï®Ìï¥Ïïº Ìï©ÎãàÎã§.');
      return;
    }

    setIsChanging(true);

    try {
      await updatePassword({
        oldPassword: currentPassword,
        newPassword: newPassword
      });

      alert('ÎπÑÎ∞ÄÎ≤àÌò∏Í∞Ä ÏÑ±Í≥µÏ†ÅÏúºÎ°ú Î≥ÄÍ≤ΩÎêòÏóàÏäµÎãàÎã§.');
      navigate('/mypage');
      
    } catch (error) {
      console.error('ÎπÑÎ∞ÄÎ≤àÌò∏ Î≥ÄÍ≤Ω Ïò§Î•ò:', error);
      
      if (error.name === 'NotAuthorizedException') {
        setError('ÌòÑÏû¨ ÎπÑÎ∞ÄÎ≤àÌò∏Í∞Ä Ïò¨Î∞îÎ•¥ÏßÄ ÏïäÏäµÎãàÎã§.');
      } else if (error.name === 'LimitExceededException') {
        setError('ÎπÑÎ∞ÄÎ≤àÌò∏ Î≥ÄÍ≤Ω ÏãúÎèÑ ÌöüÏàòÎ•º Ï¥àÍ≥ºÌñàÏäµÎãàÎã§. Ïû†Ïãú ÌõÑ Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.');
      } else if (error.name === 'InvalidPasswordException') {
        setError('ÏÉà ÎπÑÎ∞ÄÎ≤àÌò∏Í∞Ä Cognito Ï†ïÏ±ÖÏóê ÎßûÏßÄ ÏïäÏäµÎãàÎã§.');
      } else {
        setError('ÎπÑÎ∞ÄÎ≤àÌò∏ Î≥ÄÍ≤Ω Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§. Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.');
      }
    } finally {
      setIsChanging(false);
    }
  };

  return (
    <div className="change-password-container">
      {/* Ìó§Îçî ÎÑ§ÎπÑÍ≤åÏù¥ÏÖò */}
      <div className="header-navigation">
        <button 
          onClick={() => navigate('/')} 
          className="home-btn"
        >
          üè† Î©îÏù∏ÌéòÏù¥ÏßÄÎ°ú
        </button>
      </div>
      
      <div className="change-password-content">
        <h2>ÎπÑÎ∞ÄÎ≤àÌò∏ Î≥ÄÍ≤Ω</h2>
        
        <form onSubmit={handleChangePassword} className="password-form">
          <div className="form-group">
            <label htmlFor="currentPassword">ÌòÑÏû¨ ÎπÑÎ∞ÄÎ≤àÌò∏</label>
            <input
              type="password"
              id="currentPassword"
              value={currentPassword}
              onChange={(e) => setCurrentPassword(e.target.value)}
              placeholder="ÌòÑÏû¨ ÎπÑÎ∞ÄÎ≤àÌò∏Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="newPassword">ÏÉà ÎπÑÎ∞ÄÎ≤àÌò∏</label>
            <input
              type="password"
              id="newPassword"
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
              placeholder="ÏÉà ÎπÑÎ∞ÄÎ≤àÌò∏Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="confirmPassword">ÏÉà ÎπÑÎ∞ÄÎ≤àÌò∏ ÌôïÏù∏</label>
            <input
              type="password"
              id="confirmPassword"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              placeholder="ÏÉà ÎπÑÎ∞ÄÎ≤àÌò∏Î•º Îã§Ïãú ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
              required
            />
          </div>

          {error && (
            <div className="error-message">
              {error}
            </div>
          )}

          <div className="password-requirements">
            <h4>ÎπÑÎ∞ÄÎ≤àÌò∏ ÏöîÍµ¨ÏÇ¨Ìï≠:</h4>
            <ul>
              <li>ÏµúÏÜå 6Ïûê Ïù¥ÏÉÅ</li>
              <li>ÏÜåÎ¨∏Ïûê Ìè¨Ìï®</li>
              <li>Ïà´Ïûê Ìè¨Ìï®</li>
              <li>ÌäπÏàòÎ¨∏Ïûê Ìè¨Ìï® (@$!%*?&)</li>
            </ul>
          </div>

          <div className="button-group">
            <button
              type="button"
              onClick={() => navigate('/mypage')}
              className="cancel-btn"
              disabled={isChanging}
            >
              Ï∑®ÏÜå
            </button>
            <button
              type="submit"
              className="change-btn"
              disabled={isChanging}
            >
              {isChanging ? 'Î≥ÄÍ≤Ω Ï§ë...' : 'ÎπÑÎ∞ÄÎ≤àÌò∏ Î≥ÄÍ≤Ω'}
            </button>
          </div>
        </form>
      </div>

      <style jsx>{`
        .change-password-container {
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
          min-height: 100vh;
          background-color: #f5f5f5;
          padding: 24px;
          position: relative;
        }

        .header-navigation {
          position: absolute;
          top: 20px;
          left: 20px;
          z-index: 10;
        }

        .home-btn {
          padding: 10px 16px;
          background-color: #6366f1;
          color: white;
          border: none;
          border-radius: 8px;
          cursor: pointer;
          font-size: 14px;
          font-weight: 500;
          transition: background-color 0.2s;
          display: flex;
          align-items: center;
          gap: 6px;
        }

        .home-btn:hover {
          background-color: #5855eb;
        }

        .change-password-content {
          background-color: white;
          border-radius: 12px;
          box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
          padding: 40px;
          max-width: 500px;
          width: 100%;
        }

        .change-password-content h2 {
          color: #1f2937;
