import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';

// Í∞ÑÎã®Ìïú Ìôà ÌéòÏù¥ÏßÄ
function HomePage() {
  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h1>üöÄ User Service Frontend</h1>
      <p>ÌîÑÎ°†Ìä∏ÏóîÎìúÍ∞Ä Ï†ïÏÉÅÏ†ÅÏúºÎ°ú Î°úÎìúÎêòÏóàÏäµÎãàÎã§!</p>
      <div style={{ marginTop: '20px' }}>
        <h2>ÌòÑÏû¨ ÏÉÅÌÉú:</h2>
        <ul>
          <li>‚úÖ React Ïï± Î°úÎìúÎê®</li>
          <li>‚úÖ Í∏∞Î≥∏ Î†åÎçîÎßÅ ÏûëÎèô</li>
          <li>‚úÖ ÎùºÏö∞ÌåÖ ÏãúÏä§ÌÖú ÌôúÏÑ±Ìôî</li>
          <li>‚ö†Ô∏è AWS Cognito ÎπÑÌôúÏÑ±ÌôîÎê®</li>
        </ul>
      </div>
      <div style={{ marginTop: '20px', padding: '10px', backgroundColor: '#f0f0f0', borderRadius: '5px' }}>
        <p><strong>Îã§Ïùå Îã®Í≥Ñ:</strong></p>
        <p>Í∏∞Î≥∏ Í∏∞Îä•Îì§ÏùÑ Îã®Í≥ÑÎ≥ÑÎ°ú ÌôúÏÑ±ÌôîÌïòÍ≤†ÏäµÎãàÎã§.</p>
      </div>
      <div style={{ marginTop: '20px' }}>
        <Link to="/login" style={{ marginRight: '10px', padding: '10px', backgroundColor: '#007bff', color: 'white', textDecoration: 'none', borderRadius: '5px' }}>
          Î°úÍ∑∏Ïù∏
        </Link>
        <Link to="/signup" style={{ padding: '10px', backgroundColor: '#28a745', color: 'white', textDecoration: 'none', borderRadius: '5px' }}>
          ÌöåÏõêÍ∞ÄÏûÖ
        </Link>
      </div>
    </div>
  );
}

// Í∞ÑÎã®Ìïú Î°úÍ∑∏Ïù∏ ÌéòÏù¥ÏßÄ
function LoginPage() {
  const [id, setId] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async () => {
    if (!id || !password) {
      alert('ÏïÑÏù¥ÎîîÏôÄ ÎπÑÎ∞ÄÎ≤àÌò∏Î•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    try {
      const response = await fetch('http://localhost:8081/api/v1/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email_or_username: id,
          password: password
        }),
      });

      if (response.ok) {
        const data = await response.json();
        localStorage.setItem('authToken', data.access_token);
        alert('Î°úÍ∑∏Ïù∏ ÏÑ±Í≥µ!');
        // Ïó¨Í∏∞ÏÑú ÌôàÌéòÏù¥ÏßÄÎ°ú Î¶¨Îã§Ïù¥Î†âÌä∏ÌïòÍ±∞ÎÇò ÏÇ¨Ïö©Ïûê ÏÉÅÌÉúÎ•º ÏóÖÎç∞Ïù¥Ìä∏Ìï† Ïàò ÏûàÏäµÎãàÎã§
      } else {
        const errorData = await response.json();
        alert(`Î°úÍ∑∏Ïù∏ Ïã§Ìå®: ${errorData.message || 'ÏïÑÏù¥ÎîîÏôÄ ÎπÑÎ∞ÄÎ≤àÌò∏Î•º ÌôïÏù∏Ìï¥Ï£ºÏÑ∏Ïöî.'}`);
      }
    } catch (error) {
      console.error('Î°úÍ∑∏Ïù∏ Ï§ë Ïò§Î•ò Î∞úÏÉù:', error);
      alert('Î°úÍ∑∏Ïù∏ Ï§ë Î¨∏Ï†úÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§. Î∞±ÏóîÎìú ÏÑúÎ≤ÑÍ∞Ä Ïã§Ìñâ Ï§ëÏù∏ÏßÄ ÌôïÏù∏Ìï¥Ï£ºÏÑ∏Ïöî.');
    }
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif', maxWidth: '400px', margin: '0 auto' }}>
      <h2>Î°úÍ∑∏Ïù∏</h2>
      <div style={{ marginBottom: '15px' }}>
        <input
          type="text"
          placeholder="ÏïÑÏù¥Îîî"
          value={id}
          onChange={(e) => setId(e.target.value)}
          style={{ width: '100%', padding: '10px', border: '1px solid #ddd', borderRadius: '5px' }}
        />
      </div>
      <div style={{ marginBottom: '15px' }}>
        <input
          type="password"
          placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={{ width: '100%', padding: '10px', border: '1px solid #ddd', borderRadius: '5px' }}
        />
      </div>
      <button 
        onClick={handleLogin}
        style={{ width: '100%', padding: '10px', backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer' }}
      >
        Î°úÍ∑∏Ïù∏
      </button>
      <div style={{ marginTop: '15px', textAlign: 'center' }}>
        <Link to="/" style={{ color: '#007bff', textDecoration: 'none' }}>ÌôàÏúºÎ°ú ÎèåÏïÑÍ∞ÄÍ∏∞</Link>
      </div>
    </div>
  );
}

// Í∞ÑÎã®Ìïú ÌöåÏõêÍ∞ÄÏûÖ ÌéòÏù¥ÏßÄ
function SignupPage() {
  const [formData, setFormData] = useState({
    id: '',
    password: '',
    email: '',
    name: ''
  });

  const handleSignup = async () => {
    if (!formData.id || !formData.password || !formData.email || !formData.name) {
      alert('Î™®Îì† ÌïÑÎìúÎ•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    // ÏÇ¨Ïö©ÏûêÎ™Ö Í≤ÄÏ¶ù (3-20Ïûê, ÏòÅÎ¨∏/Ïà´Ïûê/Ïñ∏ÎçîÏä§ÏΩîÏñ¥Îßå)
    if (!/^[a-zA-Z0-9_]{3,20}$/.test(formData.id)) {
      alert('ÏÇ¨Ïö©ÏûêÎ™ÖÏùÄ 3-20ÏûêÏùò ÏòÅÎ¨∏, Ïà´Ïûê, Ïñ∏ÎçîÏä§ÏΩîÏñ¥Îßå ÏÇ¨Ïö© Í∞ÄÎä•Ìï©ÎãàÎã§.');
      return;
    }

    // ÎπÑÎ∞ÄÎ≤àÌò∏ Í≤ÄÏ¶ù (ÏµúÏÜå 8Ïûê, ÎåÄÏÜåÎ¨∏Ïûê/Ïà´Ïûê/ÌäπÏàòÎ¨∏Ïûê Ìè¨Ìï®)
    if (!/^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/.test(formData.password)) {
      alert('ÎπÑÎ∞ÄÎ≤àÌò∏Îäî ÏµúÏÜå 8Ïûê Ïù¥ÏÉÅÏù¥Î©∞, ÏòÅÎ¨∏ ÎåÄÏÜåÎ¨∏Ïûê, Ïà´Ïûê, ÌäπÏàòÎ¨∏Ïûê(@$!%*?&)Î•º Î™®Îëê Ìè¨Ìï®Ìï¥Ïïº Ìï©ÎãàÎã§.');
      return;
    }

    // Ïù¥Î©îÏùº Í≤ÄÏ¶ù
    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      alert('Ïò¨Î∞îÎ•∏ Ïù¥Î©îÏùº ÌòïÏãùÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    try {
      const response = await fetch('http://localhost:8081/api/v1/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: formData.id,
          password: formData.password,
          email: formData.email,
          first_name: formData.name
        }),
      });

      if (response.ok) {
        alert('ÌöåÏõêÍ∞ÄÏûÖÏù¥ ÏôÑÎ£åÎêòÏóàÏäµÎãàÎã§!');
        // ÌöåÏõêÍ∞ÄÏûÖ ÏÑ±Í≥µ ÌõÑ Î°úÍ∑∏Ïù∏ ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
        window.location.href = '/login';
      } else {
        const errorData = await response.json();
        if (errorData.details) {
          alert(`ÌöåÏõêÍ∞ÄÏûÖ Ïã§Ìå®: ${errorData.details.join(', ')}`);
        } else {
          alert('ÌöåÏõêÍ∞ÄÏûÖÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
        }
      }
    } catch (error) {
      console.error('ÌöåÏõêÍ∞ÄÏûÖ Ï§ë Ïò§Î•ò Î∞úÏÉù:', error);
      alert('ÌöåÏõêÍ∞ÄÏûÖ Ï§ë Î¨∏Ï†úÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§. Î∞±ÏóîÎìú ÏÑúÎ≤ÑÍ∞Ä Ïã§Ìñâ Ï§ëÏù∏ÏßÄ ÌôïÏù∏Ìï¥Ï£ºÏÑ∏Ïöî.');
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif', maxWidth: '400px', margin: '0 auto' }}>
      <h2>ÌöåÏõêÍ∞ÄÏûÖ</h2>
      <div style={{ marginBottom: '15px' }}>
        <input
          type="text"
          name="id"
          placeholder="ÏïÑÏù¥Îîî"
          value={formData.id}
          onChange={handleChange}
          style={{ width: '100%', padding: '10px', border: '1px solid #ddd', borderRadius: '5px' }}
        />
      </div>
      <div style={{ marginBottom: '15px' }}>
        <input
          type="password"
          name="password"
          placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏"
          value={formData.password}
          onChange={handleChange}
          style={{ width: '100%', padding: '10px', border: '1px solid #ddd', borderRadius: '5px' }}
        />
      </div>
      <div style={{ marginBottom: '15px' }}>
        <input
          type="email"
          name="email"
          placeholder="Ïù¥Î©îÏùº"
          value={formData.email}
          onChange={handleChange}
          style={{ width: '100%', padding: '10px', border: '1px solid #ddd', borderRadius: '5px' }}
        />
      </div>
      <div style={{ marginBottom: '15px' }}>
        <input
          type="text"
          name="name"
          placeholder="Ïù¥Î¶Ñ"
          value={formData.name}
          onChange={handleChange}
          style={{ width: '100%', padding: '10px', border: '1px solid #ddd', borderRadius: '5px' }}
        />
      </div>
      <button 
        onClick={handleSignup}
        style={{ width: '100%', padding: '10px', backgroundColor: '#28a745', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer' }}
      >
        ÌöåÏõêÍ∞ÄÏûÖ
      </button>
      <div style={{ marginTop: '15px', textAlign: 'center' }}>
        <Link to="/" style={{ color: '#007bff', textDecoration: 'none' }}>ÌôàÏúºÎ°ú ÎèåÏïÑÍ∞ÄÍ∏∞</Link>
      </div>
    </div>
  );
}

// ÎåÄÏãúÎ≥¥Îìú ÌéòÏù¥ÏßÄ
function DashboardPage() {
  const [userInfo, setUserInfo] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchUserInfo = async () => {
      const token = localStorage.getItem('authToken');
      if (!token) {
        window.location.href = '/login';
        return;
      }

      try {
        const response = await fetch('http://localhost:8081/api/v1/profile', {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });

        if (response.ok) {
          const data = await response.json();
          setUserInfo(data);
        } else {
          localStorage.removeItem('authToken');
          window.location.href = '/login';
        }
      } catch (error) {
        console.error('ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Ï°∞Ìöå Ï§ë Ïò§Î•ò:', error);
        localStorage.removeItem('authToken');
        window.location.href = '/login';
      } finally {
        setLoading(false);
      }
    };

    fetchUserInfo();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('authToken');
    window.location.href = '/';
  };

  if (loading) {
    return <div style={{ padding: '20px', textAlign: 'center' }}>Î°úÎî© Ï§ë...</div>;
  }

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h1>üéâ ÌôòÏòÅÌï©ÎãàÎã§!</h1>
      <div style={{ marginTop: '20px', padding: '20px', backgroundColor: '#f8f9fa', borderRadius: '10px' }}>
        <h2>ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥</h2>
        <p><strong>ÏÇ¨Ïö©ÏûêÎ™Ö:</strong> {userInfo?.username || 'N/A'}</p>
        <p><strong>Ïù¥Î¶Ñ:</strong> {userInfo?.first_name || 'N/A'}</p>
        <p><strong>Ïù¥Î©îÏùº:</strong> {userInfo?.email || 'N/A'}</p>
        <p><strong>Í∞ÄÏûÖÏùº:</strong> {userInfo?.created_at ? new Date(userInfo.created_at).toLocaleDateString() : 'N/A'}</p>
      </div>
      <div style={{ marginTop: '20px' }}>
        <button 
          onClick={handleLogout}
          style={{ padding: '10px 20px', backgroundColor: '#dc3545', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer' }}
        >
          Î°úÍ∑∏ÏïÑÏõÉ
        </button>
      </div>
    </div>
  );
}

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('authToken');
    setIsLoggedIn(!!token);
  }, []);

  return (
    <Router>
      <div>
        <nav style={{ backgroundColor: '#f8f9fa', padding: '10px 20px', borderBottom: '1px solid #ddd', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <Link to="/" style={{ color: '#007bff', textDecoration: 'none', fontSize: '18px', fontWeight: 'bold' }}>
            ‚òÅÔ∏è User Service
          </Link>
          <div>
            {isLoggedIn ? (
              <Link to="/dashboard" style={{ marginRight: '10px', color: '#007bff', textDecoration: 'none' }}>
                ÎåÄÏãúÎ≥¥Îìú
              </Link>
            ) : (
              <>
                <Link to="/login" style={{ marginRight: '10px', color: '#007bff', textDecoration: 'none' }}>
                  Î°úÍ∑∏Ïù∏
                </Link>
                <Link to="/signup" style={{ color: '#28a745', textDecoration: 'none' }}>
                  ÌöåÏõêÍ∞ÄÏûÖ
                </Link>
              </>
            )}
          </div>
        </nav>

        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/login" element={<LoginPage />} />
          <Route path="/signup" element={<SignupPage />} />
          <Route path="/dashboard" element={<DashboardPage />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
