import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';

// 간단한 홈 페이지
function HomePage() {
  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h1>🚀 User Service Frontend</h1>
      <p>프론트엔드가 정상적으로 로드되었습니다!</p>
      <div style={{ marginTop: '20px' }}>
        <h2>현재 상태:</h2>
        <ul>
          <li>✅ React 앱 로드됨</li>
          <li>✅ 기본 렌더링 작동</li>
          <li>✅ 라우팅 시스템 활성화</li>
          <li>⚠️ AWS Cognito 비활성화됨</li>
        </ul>
      </div>
      <div style={{ marginTop: '20px', padding: '10px', backgroundColor: '#f0f0f0', borderRadius: '5px' }}>
        <p><strong>다음 단계:</strong></p>
        <p>기본 기능들을 단계별로 활성화하겠습니다.</p>
      </div>
      <div style={{ marginTop: '20px' }}>
        <Link to="/login" style={{ marginRight: '10px', padding: '10px', backgroundColor: '#007bff', color: 'white', textDecoration: 'none', borderRadius: '5px' }}>
          로그인
        </Link>
        <Link to="/signup" style={{ padding: '10px', backgroundColor: '#28a745', color: 'white', textDecoration: 'none', borderRadius: '5px' }}>
          회원가입
        </Link>
      </div>
    </div>
  );
}

// 간단한 로그인 페이지
function LoginPage() {
  const [id, setId] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async () => {
    if (!id || !password) {
      alert('아이디와 비밀번호를 입력해주세요.');
      return;
    }

    try {
      const response = await fetch('http://localhost:8081/api/v1/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email_or_username: id,
          password: password
        }),
      });

      if (response.ok) {
        const data = await response.json();
        localStorage.setItem('authToken', data.access_token);
        alert('로그인 성공!');
        // 여기서 홈페이지로 리다이렉트하거나 사용자 상태를 업데이트할 수 있습니다
      } else {
        const errorData = await response.json();
        alert(`로그인 실패: ${errorData.message || '아이디와 비밀번호를 확인해주세요.'}`);
      }
    } catch (error) {
      console.error('로그인 중 오류 발생:', error);
      alert('로그인 중 문제가 발생했습니다. 백엔드 서버가 실행 중인지 확인해주세요.');
    }
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif', maxWidth: '400px', margin: '0 auto' }}>
      <h2>로그인</h2>
      <div style={{ marginBottom: '15px' }}>
        <input
          type="text"
          placeholder="아이디"
          value={id}
          onChange={(e) => setId(e.target.value)}
          style={{ width: '100%', padding: '10px', border: '1px solid #ddd', borderRadius: '5px' }}
        />
      </div>
      <div style={{ marginBottom: '15px' }}>
        <input
          type="password"
          placeholder="비밀번호"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={{ width: '100%', padding: '10px', border: '1px solid #ddd', borderRadius: '5px' }}
        />
      </div>
      <button 
        onClick={handleLogin}
        style={{ width: '100%', padding: '10px', backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer' }}
      >
        로그인
      </button>
      <div style={{ marginTop: '15px', textAlign: 'center' }}>
        <Link to="/" style={{ color: '#007bff', textDecoration: 'none' }}>홈으로 돌아가기</Link>
      </div>
    </div>
  );
}

// 간단한 회원가입 페이지
function SignupPage() {
  const [formData, setFormData] = useState({
    id: '',
    password: '',
    email: '',
    name: ''
  });

  const handleSignup = async () => {
    if (!formData.id || !formData.password || !formData.email || !formData.name) {
      alert('모든 필드를 입력해주세요.');
      return;
    }

    // 사용자명 검증 (3-20자, 영문/숫자/언더스코어만)
    if (!/^[a-zA-Z0-9_]{3,20}$/.test(formData.id)) {
      alert('사용자명은 3-20자의 영문, 숫자, 언더스코어만 사용 가능합니다.');
      return;
    }

    // 비밀번호 검증 (최소 8자, 대소문자/숫자/특수문자 포함)
    if (!/^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/.test(formData.password)) {
      alert('비밀번호는 최소 8자 이상이며, 영문 대소문자, 숫자, 특수문자(@$!%*?&)를 모두 포함해야 합니다.');
      return;
    }

    // 이메일 검증
    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      alert('올바른 이메일 형식을 입력해주세요.');
      return;
    }

    try {
      const response = await fetch('http://localhost:8081/api/v1/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: formData.id,
          password: formData.password,
          email: formData.email,
          first_name: formData.name
        }),
      });

      if (response.ok) {
        alert('회원가입이 완료되었습니다!');
        // 회원가입 성공 후 로그인 페이지로 이동
        window.location.href = '/login';
      } else {
        const errorData = await response.json();
        if (errorData.details) {
          alert(`회원가입 실패: ${errorData.details.join(', ')}`);
        } else {
          alert('회원가입에 실패했습니다.');
        }
      }
    } catch (error) {
      console.error('회원가입 중 오류 발생:', error);
      alert('회원가입 중 문제가 발생했습니다. 백엔드 서버가 실행 중인지 확인해주세요.');
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif', maxWidth: '400px', margin: '0 auto' }}>
      <h2>회원가입</h2>
      <div style={{ marginBottom: '15px' }}>
        <input
          type="text"
          name="id"
          placeholder="아이디"
          value={formData.id}
          onChange={handleChange}
          style={{ width: '100%', padding: '10px', border: '1px solid #ddd', borderRadius: '5px' }}
        />
      </div>
      <div style={{ marginBottom: '15px' }}>
        <input
          type="password"
          name="password"
          placeholder="비밀번호"
          value={formData.password}
          onChange={handleChange}
          style={{ width: '100%', padding: '10px', border: '1px solid #ddd', borderRadius: '5px' }}
        />
      </div>
      <div style={{ marginBottom: '15px' }}>
        <input
          type="email"
          name="email"
          placeholder="이메일"
          value={formData.email}
          onChange={handleChange}
          style={{ width: '100%', padding: '10px', border: '1px solid #ddd', borderRadius: '5px' }}
        />
      </div>
      <div style={{ marginBottom: '15px' }}>
        <input
          type="text"
          name="name"
          placeholder="이름"
          value={formData.name}
          onChange={handleChange}
          style={{ width: '100%', padding: '10px', border: '1px solid #ddd', borderRadius: '5px' }}
        />
      </div>
      <button 
        onClick={handleSignup}
        style={{ width: '100%', padding: '10px', backgroundColor: '#28a745', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer' }}
      >
        회원가입
      </button>
      <div style={{ marginTop: '15px', textAlign: 'center' }}>
        <Link to="/" style={{ color: '#007bff', textDecoration: 'none' }}>홈으로 돌아가기</Link>
      </div>
    </div>
  );
}

// 대시보드 페이지
function DashboardPage() {
  const [userInfo, setUserInfo] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchUserInfo = async () => {
      const token = localStorage.getItem('authToken');
      if (!token) {
        window.location.href = '/login';
        return;
      }

      try {
        const response = await fetch('http://localhost:8081/api/v1/profile', {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });

        if (response.ok) {
          const data = await response.json();
          setUserInfo(data);
        } else {
          localStorage.removeItem('authToken');
          window.location.href = '/login';
        }
      } catch (error) {
        console.error('사용자 정보 조회 중 오류:', error);
        localStorage.removeItem('authToken');
        window.location.href = '/login';
      } finally {
        setLoading(false);
      }
    };

    fetchUserInfo();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('authToken');
    window.location.href = '/';
  };

  if (loading) {
    return <div style={{ padding: '20px', textAlign: 'center' }}>로딩 중...</div>;
  }

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h1>🎉 환영합니다!</h1>
      <div style={{ marginTop: '20px', padding: '20px', backgroundColor: '#f8f9fa', borderRadius: '10px' }}>
        <h2>사용자 정보</h2>
        <p><strong>사용자명:</strong> {userInfo?.username || 'N/A'}</p>
        <p><strong>이름:</strong> {userInfo?.first_name || 'N/A'}</p>
        <p><strong>이메일:</strong> {userInfo?.email || 'N/A'}</p>
        <p><strong>가입일:</strong> {userInfo?.created_at ? new Date(userInfo.created_at).toLocaleDateString() : 'N/A'}</p>
      </div>
      <div style={{ marginTop: '20px' }}>
        <button 
          onClick={handleLogout}
          style={{ padding: '10px 20px', backgroundColor: '#dc3545', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer' }}
        >
          로그아웃
        </button>
      </div>
    </div>
  );
}

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('authToken');
    setIsLoggedIn(!!token);
  }, []);

  return (
    <Router>
      <div>
        <nav style={{ backgroundColor: '#f8f9fa', padding: '10px 20px', borderBottom: '1px solid #ddd', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <Link to="/" style={{ color: '#007bff', textDecoration: 'none', fontSize: '18px', fontWeight: 'bold' }}>
            ☁️ User Service
          </Link>
          <div>
            {isLoggedIn ? (
              <Link to="/dashboard" style={{ marginRight: '10px', color: '#007bff', textDecoration: 'none' }}>
                대시보드
              </Link>
            ) : (
              <>
                <Link to="/login" style={{ marginRight: '10px', color: '#007bff', textDecoration: 'none' }}>
                  로그인
                </Link>
                <Link to="/signup" style={{ color: '#28a745', textDecoration: 'none' }}>
                  회원가입
                </Link>
              </>
            )}
          </div>
        </nav>

        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/login" element={<LoginPage />} />
          <Route path="/signup" element={<SignupPage />} />
          <Route path="/dashboard" element={<DashboardPage />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
