import { Amplify } from 'aws-amplify';

const amplifyConfig = {
  Auth: {
    region: process.env.REACT_APP_AWS_REGION || 'us-east-1',
    userPoolId: process.env.REACT_APP_COGNITO_USER_POOL_ID,
    userPoolWebClientId: process.env.REACT_APP_COGNITO_CLIENT_ID,
    // OAuth 설정을 조건부로 추가 (도메인이 설정된 경우에만)
    ...(process.env.REACT_APP_COGNITO_DOMAIN && process.env.REACT_APP_COGNITO_DOMAIN !== 'your-cognito-domain-here' && {
      oauth: {
        domain: process.env.REACT_APP_COGNITO_DOMAIN,
        scope: ['email', 'openid', 'profile'],
        redirectSignIn: process.env.REACT_APP_REDIRECT_SIGN_IN || 'http://localhost:3000/callback',
        redirectSignOut: process.env.REACT_APP_REDIRECT_SIGN_OUT || 'http://localhost:3000',
        responseType: 'code'
      }
    })
  },
  API: {
    endpoints: [
      {
        name: 'UserServiceAPI',
        endpoint: process.env.REACT_APP_API_ENDPOINT || 'http://localhost:8081',
        region: process.env.REACT_APP_AWS_REGION || 'us-east-1'
      }
    ]
  }
};

Amplify.configure(amplifyConfig);

export default amplifyConfig;
