import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Auth } from 'aws-amplify';

const MyPage = ({ profileImage, setProfileImage, currentUser, onLogout }) => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [isDeleting, setIsDeleting] = useState(false);
  const navigate = useNavigate();

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setSelectedFile(file);
      // ÌååÏùº ÎØ∏Î¶¨Î≥¥Í∏∞ ÏÉùÏÑ±
      const reader = new FileReader();
      reader.onload = (e) => {
        setProfileImage(e.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      alert('ÌååÏùºÏùÑ ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    try {
      const formData = new FormData();
      formData.append('profile_image', selectedFile);

      const token = localStorage.getItem('authToken');
      const response = await fetch('/api/v1/users/profile-image', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
        },
        body: formData,
      });

      if (response.ok) {
        alert('ÌîÑÎ°úÌïÑ Ïù¥ÎØ∏ÏßÄÍ∞Ä ÏóÖÎ°úÎìúÎêòÏóàÏäµÎãàÎã§.');
        setSelectedFile(null);
      } else {
        alert('ÏóÖÎ°úÎìúÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
      }
    } catch (error) {
      console.error('ÏóÖÎ°úÎìú Ïò§Î•ò:', error);
      alert('ÏóÖÎ°úÎìú Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
    }
  };

  const handleDeleteAccount = async () => {
    const confirmed = window.confirm(
      'Ï†ïÎßêÎ°ú Í≥ÑÏ†ïÏùÑ ÌÉàÌá¥ÌïòÏãúÍ≤†ÏäµÎãàÍπå?\n\nÏù¥ ÏûëÏóÖÏùÄ ÎêòÎèåÎ¶¥ Ïàò ÏóÜÏúºÎ©∞, Î™®Îì† Îç∞Ïù¥ÌÑ∞Í∞Ä ÏòÅÍµ¨Ï†ÅÏúºÎ°ú ÏÇ≠Ï†úÎê©ÎãàÎã§.'
    );

    if (!confirmed) {
      return;
    }

    setIsDeleting(true);

    try {
      // AWS CognitoÏóêÏÑú ÌòÑÏû¨ ÏÇ¨Ïö©Ïûê Í≥ÑÏ†ï ÏÇ≠Ï†ú
      await Auth.deleteUser();
      
      // Î°úÏª¨ ÏÉÅÌÉú Ï†ïÎ¶¨
      localStorage.removeItem('authToken');
      localStorage.removeItem('userData');
      
      // Î°úÍ∑∏ÏïÑÏõÉ Ï≤òÎ¶¨
      if (onLogout) {
        onLogout();
      }
      
      alert('Í≥ÑÏ†ïÏù¥ ÏÑ±Í≥µÏ†ÅÏúºÎ°ú ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§.');
      navigate('/');
      
    } catch (error) {
      console.error('Í≥ÑÏ†ï ÏÇ≠Ï†ú Ïò§Î•ò:', error);
      
      if (error.code === 'NotAuthorizedException') {
        alert('Ïù∏Ï¶ùÏù¥ ÎßåÎ£åÎêòÏóàÏäµÎãàÎã§. Îã§Ïãú Î°úÍ∑∏Ïù∏Ìï¥Ï£ºÏÑ∏Ïöî.');
        if (onLogout) {
          onLogout();
        }
        navigate('/cognito-login');
      } else if (error.code === 'UserNotConfirmedException') {
        alert('Í≥ÑÏ†ïÏù¥ ÌôïÏù∏ÎêòÏßÄ ÏïäÏïòÏäµÎãàÎã§. Ïù¥Î©îÏùº ÌôïÏù∏ÏùÑ ÏôÑÎ£åÌï¥Ï£ºÏÑ∏Ïöî.');
      } else {
        alert('Í≥ÑÏ†ï ÏÇ≠Ï†ú Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§. Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.');
      }
    } finally {
      setIsDeleting(false);
    }
  };

  return (
    <div className="mypage-container">
      <div className="mypage-content">
        {/* ÌîÑÎ°úÌïÑ ÏÑπÏÖò */}
        <div className="profile-section">
          <div className="profile-image-container">
            {profileImage ? (
              <img src={profileImage} alt="ÌîÑÎ°úÌïÑ" className="profile-image" />
            ) : (
              <div className="profile-placeholder">üë§</div>
            )}
          </div>
          
          <div className="user-info">
            <h2>Welcome, {currentUser?.username || 'qwer'}</h2>
            <p>{currentUser?.username || 'qwer'}</p>
          </div>
        </div>

        {/* ÌååÏùº ÏóÖÎ°úÎìú ÏÑπÏÖò */}
        <div className="upload-section">
          <h3>ÏóÖÎ°úÎìú</h3>
          <div className="upload-controls">
            <input
              type="file"
              accept="image/*"
              onChange={handleFileChange}
              style={{ display: 'none' }}
              id="file-input"
            />
            <label htmlFor="file-input" className="file-select-link">
              ÌååÏùº ÏÑ†ÌÉù
            </label>
            <span className="selected-file">
              {selectedFile ? selectedFile.name : 'ÏÑ†ÌÉùÎêú ÌååÏùº ÏóÜÏùå'}
            </span>
          </div>
          {selectedFile && (
            <button onClick={handleUpload} className="upload-btn">
              ÏóÖÎ°úÎìú
            </button>
          )}
        </div>

        {/* Ïï°ÏÖò ÎßÅÌÅ¨Îì§ */}
        <div className="action-links">
          <button 
            onClick={() => navigate('/change-password')} 
            className="action-link"
          >
            ÎπÑÎ∞ÄÎ≤àÌò∏ Î≥ÄÍ≤Ω
          </button>
          
          <button 
            onClick={() => navigate('/myposts')} 
            className="action-link"
          >
            ÏûëÏÑ±Í∏Ä Ï°∞Ìöå
          </button>
        </div>

        {/* Í≥ÑÏ†ï ÌÉàÌá¥ Î≤ÑÌäº */}
        <div className="delete-section">
          <button 
            onClick={handleDeleteAccount}
            className="delete-btn"
          >
            Í≥ÑÏ†ï ÌÉàÌá¥
          </button>
        </div>
      </div>

      <style jsx>{`
        .mypage-container {
          display: flex;
          justify-content: center;
          align-items: center;
          min-height: 100vh;
          background-color: #f5f5f5;
          padding: 24px;
        }

        .mypage-content {
          background-color: white;
          border-radius: 12px;
          box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
          padding: 40px;
          max-width: 400px;
          width: 100%;
          text-align: center;
        }

        .profile-section {
          margin-bottom: 32px;
        }

        .profile-image-container {
          margin-bottom: 16px;
        }

        .profile-image {
          width: 60px;
          height: 60px;
          border-radius: 50%;
          object-fit: cover;
        }

        .profile-placeholder {
          width: 60px;
          height: 60px;
          border-radius: 50%;
          background-color: #6366f1;
          display: flex;
          align-items: center;
          justify-content: center;
          font-size: 24px;
          color: white;
          margin: 0 auto;
        }

        .user-info h2 {
          color: #1f2937;
          margin-bottom: 4px;
          font-size: 20px;
          font-weight: 600;
        }

        .user-info p {
          color: #1f2937;
          margin: 0;
          font-size: 16px;
        }

        .upload-section {
          margin-bottom: 32px;
          padding: 20px;
          background-color: #f9fafb;
          border-radius: 8px;
        }

        .upload-section h3 {
          color: #1f2937;
          margin-bottom: 16px;
          font-size: 16px;
          font-weight: 600;
        }

        .upload-controls {
          display: flex;
          align-items: center;
          gap: 12px;
          justify-content: center;
        }

        .file-select-link {
          color: #1f2937;
          cursor: pointer;
          font-size: 14px;
          text-decoration: none;
          font-weight: 500;
        }

        .file-select-link:hover {
          text-decoration: underline;
        }

        .selected-file {
          color: #6b7280;
          font-size: 14px;
        }

        .upload-btn {
          padding: 8px 16px;
          background-color: #6366f1;
          color: white;
          border: none;
          border-radius: 6px;
          cursor: pointer;
          font-size: 14px;
          margin-top: 12px;
        }

        .upload-btn:hover {
          background-color: #5855eb;
        }

        .action-links {
          display: flex;
          flex-direction: column;
          gap: 8px;
          margin-bottom: 24px;
        }

        .action-link {
          background: none;
          border: none;
          color: #1f2937;
          cursor: pointer;
          font-size: 16px;
          padding: 8px 0;
          text-align: center;
          text-decoration: none;
        }

        .action-link:hover {
          text-decoration: underline;
        }

        .delete-section {
          margin-top: 16px;
        }

        .delete-btn {
          width: 100%;
          padding: 12px 24px;
          background-color: #dc2626;
          color: white;
          border: none;
          border-radius: 8px;
          cursor: pointer;
          font-size: 16px;
          font-weight: 500;
        }

        .delete-btn:hover {
          background-color: #b91c1c;
        }
      `}</style>
    </div>
  );
};

export default MyPage;
