import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useNavigate } from 'react-router-dom';
import { signIn, signUp, signOut, getCurrentUser, fetchAuthSession } from 'aws-amplify/auth';

// Cognito Î°úÍ∑∏Ïù∏ ÌéòÏù¥ÏßÄ
function CognitoLoginPage() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    if (!username || !password) {
      alert('ÏÇ¨Ïö©ÏûêÎ™ÖÍ≥º ÎπÑÎ∞ÄÎ≤àÌò∏Î•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    setLoading(true);
    try {
      const { isSignedIn, nextStep } = await signIn({ username, password });
      console.log('Î°úÍ∑∏Ïù∏ ÏÑ±Í≥µ:', { isSignedIn, nextStep });
      
      if (isSignedIn) {
        // JWT ÌÜ†ÌÅ∞ÏùÑ Î°úÏª¨ Ïä§ÌÜ†Î¶¨ÏßÄÏóê Ï†ÄÏû•
        const session = await fetchAuthSession();
        const accessToken = session.tokens.accessToken.toString();
        localStorage.setItem('authToken', accessToken);
        
        alert('Î°úÍ∑∏Ïù∏ ÏÑ±Í≥µ!');
        navigate('/dashboard');
      }
    } catch (error) {
      console.error('Î°úÍ∑∏Ïù∏ Ïò§Î•ò:', error);
      alert(`Î°úÍ∑∏Ïù∏ Ïã§Ìå®: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif', maxWidth: '400px', margin: '0 auto' }}>
      <h2>AWS Cognito Î°úÍ∑∏Ïù∏</h2>
      <form onSubmit={handleLogin}>
        <div style={{ marginBottom: '15px' }}>
          <input
            type="text"
            placeholder="ÏÇ¨Ïö©ÏûêÎ™Ö"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            style={{ width: '100%', padding: '10px', border: '1px solid #ddd', borderRadius: '5px' }}
          />
        </div>
        <div style={{ marginBottom: '15px' }}>
          <input
            type="password"
            placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            style={{ width: '100%', padding: '10px', border: '1px solid #ddd', borderRadius: '5px' }}
          />
        </div>
        <button 
          type="submit"
          disabled={loading}
          style={{ 
            width: '100%', 
            padding: '10px', 
            backgroundColor: loading ? '#ccc' : '#007bff', 
            color: 'white', 
            border: 'none', 
            borderRadius: '5px', 
            cursor: loading ? 'not-allowed' : 'pointer' 
          }}
        >
          {loading ? 'Î°úÍ∑∏Ïù∏ Ï§ë...' : 'Î°úÍ∑∏Ïù∏'}
        </button>
      </form>
      <div style={{ marginTop: '15px', textAlign: 'center' }}>
        <Link to="/" style={{ color: '#007bff', textDecoration: 'none' }}>ÌôàÏúºÎ°ú ÎèåÏïÑÍ∞ÄÍ∏∞</Link>
      </div>
    </div>
  );
}

// Cognito ÌöåÏõêÍ∞ÄÏûÖ ÌéòÏù¥ÏßÄ
function CognitoSignupPage() {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    email: ''
  });
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleSignup = async (e) => {
    e.preventDefault();
    if (!formData.username || !formData.password || !formData.email) {
      alert('Î™®Îì† ÌïÑÎìúÎ•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    setLoading(true);
    try {
      const { isSignUpComplete, userId, nextStep } = await signUp({
        username: formData.username,
        password: formData.password,
        options: {
          userAttributes: {
            email: formData.email
          },
          // Client SecretÏù¥ ÏûàÎäî Í≤ΩÏö∞Î•º ÏúÑÌïú ÏÑ§Ï†ï
          clientMetadata: {
            // ÌïÑÏöîÌïú Í≤ΩÏö∞ Ïó¨Í∏∞Ïóê Ï∂îÍ∞Ä Î©îÌÉÄÎç∞Ïù¥ÌÑ∞
          }
        }
      });
      
      console.log('ÌöåÏõêÍ∞ÄÏûÖ ÏÑ±Í≥µ:', { isSignUpComplete, userId, nextStep });
      alert('ÌöåÏõêÍ∞ÄÏûÖÏù¥ ÏôÑÎ£åÎêòÏóàÏäµÎãàÎã§! Ïù¥Î©îÏùºÏùÑ ÌôïÏù∏ÌïòÏó¨ Í≥ÑÏ†ïÏùÑ ÌôúÏÑ±ÌôîÌï¥Ï£ºÏÑ∏Ïöî.');
      navigate('/cognito-login');
    } catch (error) {
      console.error('ÌöåÏõêÍ∞ÄÏûÖ Ïò§Î•ò:', error);
      alert(`ÌöåÏõêÍ∞ÄÏûÖ Ïã§Ìå®: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif', maxWidth: '400px', margin: '0 auto' }}>
      <h2>AWS Cognito ÌöåÏõêÍ∞ÄÏûÖ</h2>
      <form onSubmit={handleSignup}>
        <div style={{ marginBottom: '15px' }}>
          <input
            type="text"
            name="username"
            placeholder="ÏÇ¨Ïö©ÏûêÎ™Ö"
            value={formData.username}
            onChange={handleChange}
            style={{ width: '100%', padding: '10px', border: '1px solid #ddd', borderRadius: '5px' }}
          />
        </div>
        <div style={{ marginBottom: '15px' }}>
          <input
            type="password"
            name="password"
            placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏"
            value={formData.password}
            onChange={handleChange}
            style={{ width: '100%', padding: '10px', border: '1px solid #ddd', borderRadius: '5px' }}
          />
        </div>
        <div style={{ marginBottom: '15px' }}>
          <input
            type="email"
            name="email"
            placeholder="Ïù¥Î©îÏùº"
            value={formData.email}
            onChange={handleChange}
            style={{ width: '100%', padding: '10px', border: '1px solid #ddd', borderRadius: '5px' }}
          />
        </div>
        <button 
          type="submit"
          disabled={loading}
          style={{ 
            width: '100%', 
            padding: '10px', 
            backgroundColor: loading ? '#ccc' : '#28a745', 
            color: 'white', 
            border: 'none', 
            borderRadius: '5px', 
            cursor: loading ? 'not-allowed' : 'pointer' 
          }}
        >
          {loading ? 'ÌöåÏõêÍ∞ÄÏûÖ Ï§ë...' : 'ÌöåÏõêÍ∞ÄÏûÖ'}
        </button>
      </form>
      <div style={{ marginTop: '15px', textAlign: 'center' }}>
        <Link to="/" style={{ color: '#007bff', textDecoration: 'none' }}>ÌôàÏúºÎ°ú ÎèåÏïÑÍ∞ÄÍ∏∞</Link>
      </div>
    </div>
  );
}

// Cognito ÎåÄÏãúÎ≥¥Îìú ÌéòÏù¥ÏßÄ
function CognitoDashboardPage() {
  const [userInfo, setUserInfo] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const checkAuth = async () => {
      try {
        const user = await getCurrentUser();
        const session = await fetchAuthSession();
        const attributes = session.tokens.accessToken.payload;
        setUserInfo({
          username: user.username,
          email: attributes.email,
          name: attributes.name,
          emailVerified: attributes.email_verified
        });
      } catch (error) {
        console.error('Ïù∏Ï¶ù ÌôïÏù∏ Ïò§Î•ò:', error);
        navigate('/cognito-login');
      } finally {
        setLoading(false);
      }
    };

    checkAuth();
  }, [navigate]);

  const handleLogout = async () => {
    try {
      await signOut();
      localStorage.removeItem('authToken');
      navigate('/');
    } catch (error) {
      console.error('Î°úÍ∑∏ÏïÑÏõÉ Ïò§Î•ò:', error);
    }
  };

  if (loading) {
    return <div style={{ padding: '20px', textAlign: 'center' }}>Î°úÎî© Ï§ë...</div>;
  }

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h1>üéâ AWS Cognito ÌôòÏòÅÌï©ÎãàÎã§!</h1>
      <div style={{ marginTop: '20px', padding: '20px', backgroundColor: '#f8f9fa', borderRadius: '10px' }}>
        <h2>ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥</h2>
        <p><strong>ÏÇ¨Ïö©ÏûêÎ™Ö:</strong> {userInfo?.username || 'N/A'}</p>
        <p><strong>Ïù¥Î¶Ñ:</strong> {userInfo?.name || 'N/A'}</p>
        <p><strong>Ïù¥Î©îÏùº:</strong> {userInfo?.email || 'N/A'}</p>
        <p><strong>Ïù¥Î©îÏùº Ïù∏Ï¶ù:</strong> {userInfo?.emailVerified ? '‚úÖ Ïù∏Ï¶ùÎê®' : '‚ùå ÎØ∏Ïù∏Ï¶ù'}</p>
      </div>
      <div style={{ marginTop: '20px' }}>
        <button 
          onClick={handleLogout}
          style={{ padding: '10px 20px', backgroundColor: '#dc3545', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer' }}
        >
          Î°úÍ∑∏ÏïÑÏõÉ
        </button>
      </div>
    </div>
  );
}

// Ìôà ÌéòÏù¥ÏßÄ
function HomePage() {
  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h1>üöÄ AWS Cognito User Service</h1>
      <p>AWS CognitoÎ•º ÏÇ¨Ïö©Ìïú ÏÇ¨Ïö©Ïûê Ïù∏Ï¶ù ÏãúÏä§ÌÖúÏûÖÎãàÎã§!</p>
      <div style={{ marginTop: '20px' }}>
        <h2>ÌòÑÏû¨ ÏÉÅÌÉú:</h2>
        <ul>
          <li>‚úÖ React Ïï± Î°úÎìúÎê®</li>
          <li>‚úÖ AWS Cognito Ïó∞ÎèôÎê®</li>
          <li>‚úÖ ÎùºÏö∞ÌåÖ ÏãúÏä§ÌÖú ÌôúÏÑ±Ìôî</li>
          <li>‚úÖ Amplify ÏÑ§Ï†ï ÏôÑÎ£å</li>
        </ul>
      </div>
      <div style={{ marginTop: '20px', padding: '10px', backgroundColor: '#f0f0f0', borderRadius: '5px' }}>
        <p><strong>ÏÇ¨Ïö© Í∞ÄÎä•Ìïú Í∏∞Îä•:</strong></p>
        <ul>
          <li>üîê AWS Cognito ÌöåÏõêÍ∞ÄÏûÖ</li>
          <li>üîë AWS Cognito Î°úÍ∑∏Ïù∏</li>
          <li>üë§ ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Ï°∞Ìöå</li>
          <li>üö™ ÏïàÏ†ÑÌïú Î°úÍ∑∏ÏïÑÏõÉ</li>
        </ul>
      </div>
      <div style={{ marginTop: '20px' }}>
        <Link to="/cognito-login" style={{ marginRight: '10px', padding: '10px', backgroundColor: '#007bff', color: 'white', textDecoration: 'none', borderRadius: '5px' }}>
          Cognito Î°úÍ∑∏Ïù∏
        </Link>
        <Link to="/cognito-signup" style={{ padding: '10px', backgroundColor: '#28a745', color: 'white', textDecoration: 'none', borderRadius: '5px' }}>
          Cognito ÌöåÏõêÍ∞ÄÏûÖ
        </Link>
      </div>
    </div>
  );
}

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const checkAuth = async () => {
      try {
        await getCurrentUser();
        setIsLoggedIn(true);
      } catch (error) {
        setIsLoggedIn(false);
      }
    };

    checkAuth();
  }, []);

  return (
    <Router>
      <div>
        <nav style={{ backgroundColor: '#f8f9fa', padding: '10px 20px', borderBottom: '1px solid #ddd', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <Link to="/" style={{ color: '#007bff', textDecoration: 'none', fontSize: '18px', fontWeight: 'bold' }}>
            ‚òÅÔ∏è AWS Cognito User Service
          </Link>
          <div>
            {isLoggedIn ? (
              <Link to="/dashboard" style={{ marginRight: '10px', color: '#007bff', textDecoration: 'none' }}>
                ÎåÄÏãúÎ≥¥Îìú
              </Link>
            ) : (
              <>
                <Link to="/cognito-login" style={{ marginRight: '10px', color: '#007bff', textDecoration: 'none' }}>
                  Cognito Î°úÍ∑∏Ïù∏
                </Link>
                <Link to="/cognito-signup" style={{ color: '#28a745', textDecoration: 'none' }}>
                  Cognito ÌöåÏõêÍ∞ÄÏûÖ
                </Link>
              </>
            )}
          </div>
        </nav>
        
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/cognito-login" element={<CognitoLoginPage />} />
          <Route path="/cognito-signup" element={<CognitoSignupPage />} />
          <Route path="/dashboard" element={<CognitoDashboardPage />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
