function MyPostsPage({ isLoggedIn, profileImage, posts, currentUser }) {
  const navigate = useNavigate();

  const [activeCategory, setActiveCategory] = useState("ì „ì²´");

  const myPosts = posts.filter(
    (post) =>
      post.userName === currentUser?.userName &&
      (activeCategory === "ì „ì²´" || post.category === activeCategory)
  );

  const [currentPage, setCurrentPage] = useState(1);
  const postsPerPage = 10;

  const indexOfLastPost = currentPage * postsPerPage;
  const indexOfFirstPost = indexOfLastPost - postsPerPage;
  const currentPosts = myPosts.slice(indexOfFirstPost, indexOfLastPost);
  const totalPages = Math.ceil(myPosts.length / postsPerPage);

  const categories = ["ì „ì²´", "ë™ë¬¼/ë°˜ë ¤ë™ë¬¼", "ì—¬í–‰", "ê±´ê°•/í—¬ìŠ¤", "ì—°ì˜ˆì¸"];

  return (
    <div className="myposts-page-container">
      <header className="main-header">
        <div className="logo" onClick={() => navigate("/")}>
          â˜ï¸
        </div>
        <div className="right-header-wrapper">
          <div className="search-bar-container">{/* ê²€ìƒ‰ì°½ */}</div>
          <div className="header-actions">
            {isLoggedIn && (
              <div className="profile-container relative">
                <button className="profile-btn">
                  {profileImage ? (
                    <img
                      src={profileImage}
                      alt="Profile"
                      className="h-10 w-10 rounded-full object-cover"
                    />
                  ) : (
                    <User />
                  )}
                </button>
              </div>
            )}
            {!isLoggedIn && (
              <Link to="/login" className="login-btn">
                ë¡œê·¸ì¸
              </Link>
            )}
          </div>
        </div>
      </header>

      <div className="myposts-content-area">
        <div className="page-header-section">
          <h2 className="page-title">
            {currentUser?.userName}ì˜ ìµœì‹  ìž‘ì„±ê¸€ìž…ë‹ˆë‹¤.
          </h2>
          <div className="user-profile-icon">
            <User size={30} />
          </div>
        </div>
        <div className="category-tabs">
          {categories.map((category) => (
            <button
              key={category}
              className={`tab-btn ${
                activeCategory === category ? "active" : ""
              }`}
              onClick={() => setActiveCategory(category)}
            >
              {category}
            </button>
          ))}
        </div>
        <div className="post-list-area">
          {currentPosts.length > 0 ? (
            currentPosts.map((post) => (
              <div
                key={post.id}
                className="my-post-card"
                onClick={() => navigate(`/posts/${post.id}`, { state: post })}
                style={{ cursor: "pointer" }}
              >
                <h3 className="post-title">{post.title}</h3>
                <p className="post-content">{post.content}</p>
                <div className="post-stats">
                  <span className="stat-item">
                    <Heart size={18} />
                    <span className="stat-count">{post.likes}</span>
                  </span>
                  <span className="stat-item">
                    <MessageCircle size={18} />
                    <span className="stat-count">{post.comments}</span>
                  </span>
                </div>
              </div>
            ))
          ) : (
            <p className="no-posts-message">ìž‘ì„±í•œ ê²Œì‹œê¸€ì´ ì—†ìŠµë‹ˆë‹¤.</p>
          )}
        </div>

        {totalPages > 1 && (
          <div className="pagination flex justify-center mt-8 space-x-2">
            {[...Array(totalPages)].map((_, index) => (
              <button
                key={index + 1}
                onClick={() => setCurrentPage(index + 1)}
                className={`w-10 h-10 rounded-full flex items-center justify-center font-semibold transition-colors ${
                  currentPage === index + 1
                    ? "bg-blue-600 text-white"
                    : "bg-gray-200 text-gray-700 hover:bg-gray-300"
                }`}
              >
                {index + 1}
              </button>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

// ë©”ì¸ App ì»´í¬ë„ŒíŠ¸
function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);
  const [profileImage, setProfileImage] = useState(null);
  const [posts, setPosts] = useState([]);

  const handleLogin = (user) => {
    setIsLoggedIn(true);
    setCurrentUser(user);
    alert("ì„±ê³µì ìœ¼ë¡œ ë¡œê·¸ì¸ ë˜ì—ˆìŠµë‹ˆë‹¤!");
  };

  const handleLogout = () => {
    // í´ë¼ì´ì–¸íŠ¸ ì‚¬ì´ë“œì—ì„œë§Œ ë¡œê·¸ì•„ì›ƒ ì²˜ë¦¬
    localStorage.removeItem("authToken");
    setIsLoggedIn(false);
    setCurrentUser(null);
    setProfileImage(null);
    alert("ë¡œê·¸ì•„ì›ƒ ë˜ì—ˆìŠµë‹ˆë‹¤.");
  };
  
  const addPost = (newPost) => {
    setPosts((prevPosts) => [newPost, ...prevPosts]); // âœ… ìƒˆ ê²Œì‹œë¬¼ ì¶”ê°€ í•¨ìˆ˜
  };

  return (
    <Router>
      <Routes>
        <Route
          path="/"
          element={
            <MainBoardPage
              isLoggedIn={isLoggedIn}
              onLogout={handleLogout}
              profileImage={profileImage}
              posts={posts} // âœ… posts ìƒíƒœ ì „ë‹¬
              setPosts={setPosts} // âœ… setPosts í•¨ìˆ˜ ì „ë‹¬
              currentUser={currentUser}
            />
          }
        />
        <Route
          path="/MyPage"
          element={
            <MyPage
              profileImage={profileImage}
              setProfileImage={setProfileImage}
              currentUser={currentUser}
              onLogout={handleLogout}
            />
          }
        />
        <Route
          path="/change-password"
          element={<ChangePasswordPage currentUser={currentUser} />}
        />
        <Route path="/findid" element={<FindIdentificationPage />} />
        <Route path="/findpassword" element={<FindPasswordPage />} />
        <Route path="/signup" element={<SignUpPage />} />
        <Route
          path="/new-post"
          element={
            <NewPostPage
              isLoggedIn={isLoggedIn}
              profileImage={profileImage}
              onAddPost={addPost}
              currentUser={currentUser}
            />
          }
        />
        <Route
          path="/posts/:id"
          element={
            <PostDetailPage
              isLoggedIn={isLoggedIn}
              onLogout={handleLogout}
              profileImage={profileImage}
              posts={posts}
              currentUser={currentUser}
            />
          }
        />
        <Route path="/login" element={<LoginPage onLogin={handleLogin} />} />
        <Route
          path="/myposts"
          element={
            <MyPostsPage
              isLoggedIn={isLoggedIn}
              profileImage={profileImage}
              posts={posts}
              currentUser={currentUser}
            />
          }
        />
      </Routes>
    </Router>
  );
}

export default App;
