import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useNavigate } from 'react-router-dom';
import { signIn, signUp, signOut, getCurrentUser, fetchAuthSession } from 'aws-amplify/auth';

// Import new components
import MainBoardPage from './pages/MainBoardPage';
import LoginPage from './pages/LoginPage';
import SignUpPage from './pages/SignUpPage';
import MyPage from './pages/MyPage';

// Cognito 로그인 페이지 (기존)
function CognitoLoginPage() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    if (!username || !password) {
      alert('사용자명과 비밀번호를 입력해주세요.');
      return;
    }

    setLoading(true);
    try {
      const { isSignedIn, nextStep } = await signIn({ username, password });
      console.log('로그인 성공:', { isSignedIn, nextStep });

      if (isSignedIn) {
        // JWT 토큰을 로컬 스토리지에 저장
        const session = await fetchAuthSession();
        const accessToken = session.tokens.accessToken.toString();
        localStorage.setItem('authToken', accessToken);

        alert('로그인 성공!');
        navigate('/dashboard');
      }
    } catch (error) {
      console.error('로그인 오류:', error);
      alert(`로그인 실패: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif', maxWidth: '400px', margin: '0 auto' }}>
      <h2>AWS Cognito 로그인</h2>
      <form onSubmit={handleLogin}>
        <div style={{ marginBottom: '15px' }}>
          <input
            type="text"
            placeholder="사용자명"
            value={username}
